import classes from './cardForm.module.sass'
import {useFormik, Formik, Field, Form, yupToFormErrors} from "formik";
import * as yup from 'yup'

const CardForm = () => {
    const validShema = yup.object().shape({
        text: yup.string().min(2, 'Минимум 2 символа').required('Обязательно')
    })

    return (
        // <form className={classes.formCard} onSubmit={formik.handleSubmit}>
        //     <h2>Create card</h2>
        //     <input type="text" name='text' onChange={formik.handleChange} value={formik.values.text}/>
        // <button type="submit">PUSH</button>
        // </form>
        <Formik initialValues={{
            text:'',

        }}
                validationSchema={validShema}
                onSubmit={
            values => console.log(values)
        }>

            {({ errors, touched})=>(
                <Form>
                    <h2>Create card</h2>
                    <Field name="text" type='text'></Field>
                    {errors.text && touched.text ? (
                        <div>{errors.text}</div>
                    ) : null}
                    <button type="submit">PUSH</button>
                </Form>
                )}
        </Formik>
    )

}

export default CardForm
